import { createSlice } from '@reduxjs/toolkit';

// Load initial state from localStorage, or start with an empty array if none exists
const loadFromLocalStorage = () => {
  const savedCart = localStorage.getItem('cart');
  
  return savedCart ? JSON.parse(savedCart) : [];
};

const saveToLocalStorage = (state) => {
  localStorage.setItem('cart', JSON.stringify(state));
};

const cartSlice = createSlice({
  name: 'cart',
  initialState: loadFromLocalStorage(),
  reducers: {
    addToCart: (state, action) => {
      const existingItem = state.find(item => item.id === action.payload.id);
      if (existingItem) {
        existingItem.quantity += 1;
      } else {
        state.push({ ...action.payload, quantity: 1 });
      }
      saveToLocalStorage(state); // Save updated state to localStorage
    },
    removeFromCart: (state, action) => {
      const updatedState = state.filter(item => item.id !== action.payload.id);
      saveToLocalStorage(updatedState); // Save updated state to localStorage
      return updatedState;
    },
    incrementQuantity: (state, action) => {
      const item = state.find(item => item.id === action.payload.id);
      if (item) {
        item.quantity += 1;
        saveToLocalStorage(state); // Save updated state to localStorage
      }
    },
    decrementQuantity: (state, action) => {
      const item = state.find(item => item.id === action.payload.id);
      if (item && item.quantity > 1) {
        item.quantity -= 1;
        saveToLocalStorage(state); // Save updated state to localStorage
      }
    },
  },
});

export const { addToCart, removeFromCart, incrementQuantity, decrementQuantity } = cartSlice.actions;

export default cartSlice.reducer;
