import { useSelector } from 'react-redux';
import { Header } from './Header';
import tableImage from '../Assets/table.jpg';
import { BigDiscount } from './BigDiscount';
export const ProductDetails = () => {
  const selectedProduct = useSelector((state) => state.product.selectedProduct);
  const relatedProducts = BigDiscount.filter(
    (product) => product.category === selectedProduct.category && product.id !== productId
  );
  // Return a loading or fallback UI if selectedProduct is not available
  if (!selectedProduct) {
    return (
      <div className="container mt-5">
        <Header />
        <p className="text-center">Loading product details...</p>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <Header />
     <div className="position-relative" >
  <img src={tableImage} alt="table" className="col-12" />

  <div
    className="position-absolute top-0 start-0 w-100 h-100"
    style={{
      backgroundColor: 'rgba(0, 0, 0, 0.5)', 
      pointerEvents: 'none', 
    }}
  ></div>
  <div 
    className="position-absolute top-50 start-50 translate-middle text-white text-center"
    style={{ fontSize: '24px', fontWeight: 'bold',textShadow: '2px 2px 4px rgba(222, 31, 31, 0.8)',  }}
  >
   
   <h3>{selectedProduct.productName}</h3>
  </div>
</div>

      
      <div className="row">
        <div className="col-md-6">
          <img
            src={selectedProduct.imgUrl}
            alt={selectedProduct.productName}
            className="img-fluid"
          />
        </div>
        <div className="col-md-6">
          <h1 className=" me-5 mt-2">{selectedProduct.productName}</h1>
         <h3> <span>{selectedProduct.avgRating}</span>
          <span>{selectedProduct.avgRating}</span>
          <span>{selectedProduct.avgRating}</span>
          <span>{selectedProduct.avgRating}</span>
          <span>{selectedProduct.avgRating}</span>
          <span className='ms-5'>{selectedProduct.rating}</span>
          </h3>
          <h2>
         <span> {selectedProduct.price}</span> 
         <span className='fs-5 ms-5 me-5'>cateogry:{selectedProduct.category}
          </span></h2>
          <p>{selectedProduct.shortDesc}</p>
          <div>
          <button style={{width:"100px",height:"30px"}}></button>
          </div>
          <button className="btn btn-primary mt-2">Add to Cart</button>
          </div>
            <h5><b>Description:</b> </h5>
          <p>{selectedProduct.description}</p>
      </div>
      <h1>You might also like</h1>

    </div>
  );
};
