import { useDispatch, useSelector } from "react-redux"
import { Header } from "./Header"

import { DecQuantity, IncQuantity, REMOVEFROMCART } from "../redux/cartSlice"

export const Cart = () => {
  let cart = useSelector(state => state.cart)
  let dispatch = useDispatch()
  return (
    <div className="className">
      <Header />
      <h1>CartPage</h1>
      {
        (cart.length > 0) ? (

          <div>
            {
              cart.map(product => (
                <div className="row border m-2">
                  <div className="col-3 p-3">
                    <img src={product.imgUrl} alt="" width={'200px'} height={'200px'} className="bg-light" />

                  </div>
                  <div className="col-3">
                    <h5>{product.productName}</h5>
                    <strong className="text-info p-2 mt-3 ">{product.price}*{product.quantity}</strong>
                    <strong className="bg-primary p-2 mt-5 rounded text-light">{product.price}</strong>
                    </div>
                  
                    <div className="col-3">

                    <button className="btn btn-danger mt-5" onClick={() => dispatch(REMOVEFROMCART(product.id))}>x</button>
                      <button className="btn btn-secondary mt-5" onClick={() => dispatch(IncQuantity(product.id))}>+</button>
                     
                      <button className="btn btn-secondary mt-5" onClick={() => dispatch(DecQuantity(product.id))}>-</button>
                    
                      
                    </div>

                  </div>
                
              ))
            }
          </div>


        ) : (<h1>UR CART IS EMPTY</h1>)
      }
    </div>
  )
}
