import React, { useState, useEffect } from "react";
import { Header } from "./Header";
import { BigDiscount } from "./BigDiscount";
import { NewArrivals } from "./NewArrivals";
import { BestSales } from "./BestSales";
import { ADDTOCART } from "../redux/cartSlice";
import { useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
export const Shop = () => {
   
  let {id}= useParams()
  console.log(id)
  
  let dispatch = useDispatch()
  let handleAddToCart = (product) => {
    dispatch(ADDTOCART({ ...product, quantity: 1 }));
  };
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("sofa"); // Default to sofa

  const categories = ["sofa", "chair", "mobile", "wireless"];

  // Fetch and set products on component mount
  useEffect(() => {
    const products = [...BigDiscount, ...NewArrivals, ...BestSales];
    setAllProducts(products);

    // Filter products by the default category (sofa)
    const defaultFiltered = products.filter(
      (product) => product.category === "sofa"
    );
    setFilteredProducts(defaultFiltered);
  }, []);

  // Handle category selection
  const handleCategoryChange = (category) => {
    setSelectedCategory(category);

    // Filter based on the selected category and the search term
    const filtered = allProducts.filter(
      (product) =>
        product.category === category &&
        product.productName.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredProducts(filtered);
  };

  // Handle search input
  const handleSearch = (term) => {
    setSearchTerm(term);

    // Filter based on the selected category and the search term
    const filtered = allProducts.filter(
      (product) =>
        product.category === selectedCategory &&
        product.productName.toLowerCase().includes(term.toLowerCase())
    );
    setFilteredProducts(filtered);
  };

  return (
    <div className="shop">
      <Header />

      {/* Category Dropdown and Search Bar */}
      <div className="d-flex ms-5" style={{ marginLeft: "20px" }}>
        {/* Category Dropdown */}
        <div className="dropdown">
          <button
            className="btn btn-secondary dropdown-toggle mt-5"
            type="button"
            id="dropdownMenuButton1"
            data-bs-toggle="dropdown"
            aria-expanded="false"
          >
            Find By Category
          </button>
          <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton1">
            {categories.map((category) => (
              <li key={category}>
                <button
                  className={`dropdown-item ${
                    selectedCategory === category ? "active" : ""
                  }`}
                  onClick={() => handleCategoryChange(category)}
                >
                  {category.charAt(0).toUpperCase() + category.slice(1)}
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Search Bar */}
        <div className="ms-5 col-9">
          <form className="mt-5">
            <div
              className="d-flex"
              style={{ position: "relative", marginLeft: "170px" }}
            >
              <input
                type="search"
                className="form-control rounded-pill"
                style={{
                  backgroundColor: "lightgray",
                  paddingRight: "40px",
                }}
                placeholder="Search..."
                value={searchTerm}
                onChange={(e) => handleSearch(e.target.value)}
                aria-label="Search"
              />
              <i
                className="bi bi-search"
                style={{
                  position: "absolute",
                  right: "15px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  cursor: "pointer",
                }}
              ></i>
            </div>
          </form>
        </div>
      </div>

      {/* Product Grid */}
      <div className="products mt-5">
        {filteredProducts.length > 0 ? (
          <div className="row">
            {filteredProducts.map((product) => (
              <div key={product.id} className="col-md-4 product-card">
                <div className="card">
                  <img
                    src={product.imgUrl}
                    alt={product.productName}
                    className="card-img-top mt-3"
                    style={{ height: "300px", objectFit: "cover" }}
                  />
                  <div className="card-body">
                    <h2 className="card-title">{product.productName}</h2>
                    <div style={{ display: 'flex', gap: '10px' }}>
                        <span className="card-text text-warning">{product.avgRating}</span>
                        <span className="card-text text-warning">{product.avgRating}</span>
                        <span className="card-text text-warning">{product.avgRating}</span>
                        <span className="card-text text-warning">{product.avgRating}</span>
                        <span className="card-text text-warning">{product.avgRating}</span>
                      </div>
                      <div className="d-flex align-items-center justify-content-between">
                        <h3 className="card-text"><b>{product.price}</b> </h3>

                       
                        <button
                          className="btn btn-light btn-outline-primary d-flex align-items-center justify-content-center rounded-circle" 
                          style={{
                            width: "50px",
                            height: "50px",
                          }}
                          onClick={()=>handleAddToCart(product)}
                        >
                          <span style={{ fontSize: "40px", marginBottom: "5px" }}>+</span>
                        </button>


                      </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-center mt-5">No products found for this category</p>
        )}
      </div>
    </div>
  );
};
