import { Header } from "./Header"
import tableImage from '../Assets/table.jpg';
import { useSelector } from "react-redux";
import { BigDiscount } from "./BigDiscount";
import { NewArrivals } from "./NewArrivals";
import { useState,useEffect } from "react";
export const Shop = () => {
  
      const [filteredProducts, setFilteredProducts] = useState([]);
      const [allProducts, setAllProducts] = useState([]);
      const [searchTerm, setSearchTerm] = useState("");
      const [selectedCategory, setSelectedCategory] = useState("sofa"); // Default category set to "sofa"
    
      const categories = ["sofa", "chair", "mobile", "wireless"];
    
      // Initialize product data
      useEffect(() => {
        const products = [...BigDiscount, ...NewArrivals];
        setAllProducts(products);
    
        // Default filter by "sofa"
        const defaultFiltered = products.filter(
          (product) => product.category === "sofa"
        );
        setFilteredProducts(defaultFiltered);
      }, []);
    
      // Handle category filtering
      const handleCategoryChange = (category) => {
        setSelectedCategory(category); // Update selected category state
        const filtered = allProducts.filter(
          (product) => product.category === category
        );
        setFilteredProducts(filtered);
      };
    
      // Handle search filtering
      const handleSearch = (term) => {
        setSearchTerm(term);
        const filtered = allProducts.filter(
          (product) =>
            product.category === selectedCategory &&
            product.productName.toLowerCase().includes(term.toLowerCase())
        );
        setFilteredProducts(filtered);
      };
    
      return (
        <div className="shop">
        
    
          {/* Filter & Search Section */}
          <div className="d-flex ms-5" style={{ marginLeft: "20px" }}>
            {/* Category Filter */}
            <div className="dropdown">
              <button
                className="btn btn-secondary dropdown-toggle mt-5"
                type="button"
                id="dropdownMenuButton1"
                data-bs-toggle="dropdown"
                aria-expanded="false"
              >
                Find By Category
              </button>
              <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                {categories.map((category) => (
                  <li key={category}>
                    <button
                      className="dropdown-item"
                      onClick={() => handleCategoryChange(category)}
                    >
                      {category.charAt(0).toUpperCase() + category.slice(1)}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
    
            {/* Search Bar */}
            <div className="ms-5 col-9">
              <form className="mt-5">
                <div
                  className="d-flex"
                  style={{ position: "relative", marginLeft: "170px" }}
                >
                  <input
                    type="search"
                    className="form-control rounded-pill"
                    style={{
                      backgroundColor: "lightgray",
                      paddingRight: "40px",
                    }}
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={(e) => handleSearch(e.target.value)}
                    aria-label="Search"
                  />
                  <i
                    className="bi bi-search"
                    style={{
                      position: "absolute",
                      right: "15px",
                      top: "50%",
                      transform: "translateY(-50%)",
                      cursor: "pointer",
                    }}
                  ></i>
                </div>
              </form>
            </div>
          </div>
    
          {/* Products Display Section */}
          <div className="products mt-5">
            {filteredProducts.length > 0 ? (
              <div className="row">
                {filteredProducts.map((product) => (
                  <div key={product.id} className="col-md-4 product-card">
                    <div className="card">
                      <img
                        src={product.imgUrl}
                        alt={product.productName}
                        className="card-img-top"
                      />
                      <div className="card-body">
                        <h5 className="card-title">{product.productName}</h5>
                        <p className="card-text">{product.shortDesc}</p>
                        <p className="text-primary">{product.price}</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-center mt-5">No products found for this category</p>
            )}
          </div>
        </div>
      );
    };
