import { createSlice } from '@reduxjs/toolkit';

// Helper function to load cart from localStorage
const loadFromLocalStorage = () => {
  try {
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  } catch (error) {
    console.error('Failed to load cart from localStorage:', error);
    return [];
  }
};

const initialCartState = {
  items: loadFromLocalStorage(),
  totalPrice: 0, // Ensure totalPrice is initialized as 0 (number)
};

const cartSlice = createSlice({
  name: 'cart',
  initialState: initialCartState,
  reducers: {
    addToCart: (state, action) => {
      const existingItem = state.items.find((item) => item.id === action.payload.id);
      if (existingItem) {
        existingItem.quantity += 1;
      } else {
        state.items.push({ ...action.payload, quantity: 1 });
      }
      state.totalPrice = Number(state.totalPrice) + Number(action.payload.price); // Ensure it's a number
    },
    removeFromCart: (state, action) => {
      const item = state.items.find((item) => item.id === action.payload.id);
      if (item) {
        state.totalPrice = Number(state.totalPrice) - item.price * item.quantity; // Ensure it's a number
        state.items = state.items.filter((item) => item.id !== action.payload.id);
      }
    },
    incrementQuantity: (state, action) => {
      const item = state.items.find((item) => item.id === action.payload.id);
      if (item) {
        item.quantity += 1;
        state.totalPrice = Number(state.totalPrice) + Number(item.price); // Ensure it's a number
      }
    },
    decrementQuantity: (state, action) => {
      const item = state.items.find((item) => item.id === action.payload.id);
      if (item && item.quantity > 1) {
        item.quantity -= 1;
        state.totalPrice = Number(state.totalPrice) - Number(item.price); // Ensure it's a number
      }
    },
  },
});

export const { addToCart, removeFromCart, incrementQuantity, decrementQuantity } = cartSlice.actions;

export default cartSlice.reducer;
