import { useSelector, useDispatch } from 'react-redux';
import { incrementQuantity, decrementQuantity, removeFromCart } from '../redux/cartSlice';
import { Header } from './Header';

export const Cart = () => {
  const cart = useSelector((state) => state.cart.items);
   // Access items in the cart
  const totalPrice = useSelector((state) => state.cart.totalPrice); // Access total price
  const dispatch = useDispatch();

  return (
    <div className="container mt-5">
      <Header />
      <h1>Your Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty!</p>
      ) : (
        <div>
          <div className="row">
            {cart.map((item) => (
              <div className="col-md-4" key={item.id}>
                <div className="card h-100">
                  <img
                    src={item.imgUrl}
                    className="card-img-top"
                    alt={item.productName}
                    style={{ height: '200px', objectFit: 'cover' }}
                  />
                  <div className="card-body">
                    <h2 className="card-title">{item.productName}</h2>
                    <p className="card-text">
                      <b>Price:</b> {item.price}
                    </p>
                    <p className="card-text">
                      <b>Quantity:</b> {item.quantity}
                    </p>
                    <div className="d-flex justify-content-between">
                      <button
                        className="btn btn-success"
                        onClick={() => dispatch(incrementQuantity({ id: item.id }))}
                      >
                        +
                      </button>
                      <button
                        className="btn btn-danger"
                        onClick={() => dispatch(decrementQuantity({ id: item.id }))}
                        disabled={item.quantity === 1}
                      >
                        -
                      </button>
                    </div>
                    <button
                      className="btn btn-outline-danger mt-2"
                      onClick={() => dispatch(removeFromCart({ id: item.id }))}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <h2 className="mt-4">
            <b>Total Price:</b> ${totalPrice.toFixed(2)} {/* Display total price */}
          </h2>
        </div>
      )}
    </div>
  );
};
