import { useSelector } from 'react-redux';
import { Header } from './Header';
import { BigDiscount } from './BigDiscount';

export const ProductDetails = () => {
  const selectedProduct = useSelector((state) => state.product.selectedProduct);

  // Return a loading or fallback UI if selectedProduct is not available
  if (!selectedProduct) {
    return (
      <div className="container mt-5">
        <Header />
        <p className="text-center">Loading product details...</p>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <Header />
      <div className="row">
        <div className="col-md-6">
          <img
            src={selectedProduct.imgUrl}
            alt={selectedProduct.productName}
            className="img-fluid"
          />
        </div>
        <div className="col-md-6">
          <h1 className="text-center">{selectedProduct.productName}</h1>
          <span>{selectedProduct.avgRating}</span>
          <span>{selectedProduct.avgRating}</span>
          <span>{selectedProduct.avgRating}</span>
          <span>{selectedProduct.avgRating}</span>
          <span>{selectedProduct.avgRating}</span>
          {BigDiscount.map((review) => (
  
    <strong>{review.rating}:</strong> 

))}


          <h3>Price: ${selectedProduct.price}</h3>
          <p>{selectedProduct.shortDesc}</p>
          <p>{selectedProduct.description}</p>
          <h4>Discount: {selectedProduct.discount}%</h4>
          <button className="btn btn-primary">Add to Cart</button>
        </div>
      </div>
    </div>
  );
};
