import { useDispatch, useSelector } from "react-redux";
import { Header } from "./Header";
import { DecQuantity, IncQuantity, REMOVEFROMCART } from "../redux/cartSlice";

export const Cart = () => {
  const cart = useSelector((state) => state.cart);
  const dispatch = useDispatch();

  // Calculate total price
  const totalPrice = cart.reduce(
    (total, product) => total + product.price * product.quantity,
    0
  );

  return (
    <div className="cart-page">
      <Header />
      <div className="container mt-5">
        <h1 className="text-center mb-4">Cart Page</h1>

        <div className="row">
          {/* Cart Items */}
          <div className="col-md-8">
            {cart.length > 0 ? (
              cart.map((product) => (
                <div
                  key={product.id}
                  className="row align-items-center mb-4 p-3 bg-light rounded shadow-sm"
                >
                  <div className="col-md-3 text-center">
                    <img
                      src={product.imgUrl}
                      alt={product.productName}
                      className="img-fluid rounded"
                      style={{ maxHeight: "120px" }}
                    />
                  </div>
                  <div className="col-md-4">
                    <h5>{product.productName}</h5>
                    <p className="text-muted">
                      ${product.price} x {product.quantity}
                    </p>
                    <strong className="text-primary">
                      ${product.price * product.quantity}
                    </strong>
                  </div>
                  <div className="col-md-3 text-center">
                    <button
                      className="btn btn-outline-secondary mx-1"
                      onClick={() => dispatch(DecQuantity(product.id))}
                    >
                      -
                    </button>
                    <strong className="mx-2">{product.quantity}</strong>
                    <button
                      className="btn btn-outline-secondary mx-1"
                      onClick={() => dispatch(IncQuantity(product.id))}
                    >
                      +
                    </button>
                  </div>
                  <div className="col-md-2 text-center">
                    <button
                      className="btn btn-danger"
                      onClick={() => dispatch(REMOVEFROMCART(product.id))}
                    >
                      X
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <h3 className="text-center text-danger">Your Cart is Empty</h3>
            )}
          </div>

          {/* Summary Section */}
          <div className="col-md-4">
            <div className="card shadow-sm p-3">
              <h4 className="card-title">Cart Summary</h4>
              <div className="card-body">
                <h5 className="card-text">
                  Total Price:{" "}
                  <span className="text-primary">${totalPrice.toFixed(2)}</span>
                </h5>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="bg-dark text-light mt-5 py-4">
        <div className="container">
          <div className="row">
            <div className="col-md-3">
              <h5>Mart</h5>
              <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Auctor
                libero id et.
              </p>
            </div>
            <div className="col-md-3">
              <h5>About Us</h5>
              <ul className="list-unstyled">
                <li>Careers</li>
                <li>Our Stores</li>
                <li>Terms & Conditions</li>
                <li>Privacy Policy</li>
              </ul>
            </div>
            <div className="col-md-3">
              <h5>Customer Care</h5>
              <ul className="list-unstyled">
                <li>Help Center</li>
                <li>How to Buy</li>
                <li>Track Your Order</li>
                <li>Returns & Refunds</li>
              </ul>
            </div>
            <div className="col-md-3">
              <h5>Contact Us</h5>
              <p>
                70 Washington Square South, NY 10012, United States
                <br />
                Email: example@gmail.com
                <br />
                Phone: +1 1123 456 780
              </p>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
};
