// src/redux/store.js
import { configureStore } from '@reduxjs/toolkit';
import productReducer from './productSlice';
import cartReducer from './cartSlice'

const saveCartToLocalStorageMiddleware = (storeAPI) => (next) => (action) => {
  const result = next(action); // Process the action
  const cart = storeAPI.getState().cart; // Get updated cart state
  localStorage.setItem('cart', JSON.stringify(cart)); // Save cart to localStorage
  return result; // Return the result of the action
};
export const store = configureStore({
  reducer: {
    product: productReducer,
    cart:cartReducer
  },
  middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware().concat(saveCartToLocalStorageMiddleware),
});
