import React, { useState } from 'react';
import { BigDiscount } from './BigDiscount';  // Import BigDiscount data
import { NewArrivals } from './NewArrivals'; // Import NewArrivals data

const Shop = () => {
  // State for filtered products
  const [filteredProducts, setFilteredProducts] = useState([]);
  
  // Categories array
  const categories = ['sofa', 'chair', 'mobile', 'headphones'];
  
  // Function to handle category filter
  const handleCategoryClick = (category) => {
    // Combine all products from BigDiscount and NewArrivals
    const allProducts = [...BigDiscount, ...NewArrivals];
    
    // Filter products based on the selected category
    const filtered = allProducts.filter(product => product.category === category);
    setFilteredProducts(filtered); // Update state with filtered products
  };

  return (
    <div className="shop">
      <div className="categories">
        {/* Render category buttons */}
        {categories.map((category) => (
          <button key={category} onClick={() => handleCategoryClick(category)}>
            {category.charAt(0).toUpperCase() + category.slice(1)} {/* Capitalize category */}
          </button>
        ))}
      </div>

      <div className="products">
        {/* Render filtered products */}
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.imgUrl} alt={product.productName} />
              <h3>{product.productName}</h3>
              <p>{product.price}</p>
              <p>{product.shortDesc}</p>
            </div>
          ))
        ) : (
          <p>No products found for this category</p>
        )}
      </div>
    </div>
  );
};

export default Shop;
