import { useDispatch, useSelector } from "react-redux"
import { Header } from "./Header"
// import { DecQuantity, IncQuantity, REMOVEFROMCART } from "../../store/CartOpe"

export const Cart = () => {
    let cart = useSelector(state => state.cart)
    let dispatch = useDispatch()
    return (
        <div className="className">
            <Header />
            <h1>CartPage</h1>
            {
                (cart.length > 0) ? (

                    <div>
                        {
                            cart.map(product=>(
                                <div className="row border m-2">
                                <div className="col-md-6 p-3">
                              <img src={product.image} alt="" width={'200px'} height={'200px'} className="bg-info"/>
                             
                                </div>
                                <div className="col-md-6">
                                    <h5>{product.title}</h5>
                                    {/* <h5>{product.description}</h5> */}
                                    <strong className="bg-primary p-2 rounded text-light">{product.price}</strong>
                                    <span>{product.ratting}</span>
                                  
                                      {/* <div>
                                        
                                     <button className="btn btn-secondary mt-5" onClick={()=>dispatch(IncQuantity(product.id))}>+</button>
                                     <strong className = "bg-dark text-light p-2 rounded">{product.quantity}</strong>
                                     <button className="btn btn-secondary mt-5" onClick={()=>dispatch(DecQuantity(product.id))}>-</button>
                                     <button className="btn btn-danger mt-5" onClick={()=>dispatch(REMOVEFROMCART(product.id))}>REMOVE</button>
                                      </div> */}
                                  
                                </div>
                            </div>  
                            ))
                        }
                    </div>


                ) : (<h1>UR CART IS EMPTY</h1>)
            }
        </div>
    )
}
