import { useEffect } from "react";
import { Link } from "react-router-dom";

export const Header = () => {
 useEffect(() => {

    const carouselElement = document.getElementById("headerCarousel");
    if (carouselElement && window.bootstrap) {
      new window.bootstrap.Carousel(carouselElement, {
        interval: 3000,
        ride: "carousel",
      });
    }
  }, []);
  return (
    <div className="Container">
      {/* Navbar */}
      <nav className="navbar navbar-expand-sm navbar-dark bg-light">
        <h2>
          <i className="bi bi-bag-fill ms-5"></i>
        </h2>
        <div className="container-fluid text-dark">
          <a className="navbar-brand text-dark" href="javascript:void(0)">
            <h2>Mart</h2>
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#mynavbar"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <form className="me-5">
            <div className="collapse navbar-collapse" id="navbarNavDropdown">
              <ul className="navbar-nav">
                <li className="nav-item">
                  <Link className="nav-link active text-dark" aria-current="page" to="/">
                    <h5>Home</h5>
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link active text-dark" aria-current="page" to="/Shop">
                    <h5>Shop</h5>
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link active text-dark" aria-current="page" to="/Cart">
                    <h5>Cart</h5>
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link active text-dark" aria-current="page" to="/SignUp">
                    <h5>
                      <i className="bi bi-person-fill"></i>
                    </h5>
                  </Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link active text-dark font" aria-current="page" to="/CartIcon">
                    <h5>
                      <i className="bi bi-cart-fill"></i>
                    </h5>
                  </Link>
                </li>
              </ul>
            </div>
          </form>
        </div>
      </nav>

      {/* Automatic Carousel */}
      <div id="headerCarousel" className="carousel slide mt-4" data-bs-ride="carousel" data-bs-interval="3000">
        <div className="carousel-inner">
          {/* Carousel Item 1 */}
          <div className="carousel-item active">
            <div className="d-flex align-items-center justify-content-between">
              <div className="text-container p-5">
                <h2 className="fw-bold">Welcome to Mart</h2>
                <p className="fs-5">
                  Discover a wide range of quality products at unbeatable prices. Shop now to get
                  amazing deals!
                </p>
                <Link to="/Shop" className="btn btn-primary">
                  Start Shopping
                </Link>
              </div>
              <div className="image-container">
                <img
                  src="https://via.placeholder.com/800x400"
                  className="d-block w-100"
                  alt="Product Promo"
                />
              </div>
            </div>
          </div>

          {/* Carousel Item 2 */}
          <div className="carousel-item">
            <div className="d-flex align-items-center justify-content-between">
              <div className="text-container p-5">
                <h2 className="fw-bold">Exclusive Discounts</h2>
                <p className="fs-5">
                  Get exclusive discounts on our best-selling products. Limited-time offers!
                </p>
                <Link to="/Cart" className="btn btn-secondary">
                  View Your Cart
                </Link>
              </div>
              <div className="image-container">
                <img
                  src="https://via.placeholder.com/800x400"
                  className="d-block w-100"
                  alt="Discount Banner"
                />
              </div>
            </div>
          </div>

          {/* Carousel Item 3 */}
          <div className="carousel-item">
            <div className="d-flex align-items-center justify-content-between">
              <div className="text-container p-5">
                <h2 className="fw-bold">Sign Up Today</h2>
                <p className="fs-5">
                  Create an account to unlock personalized recommendations and faster checkouts.
                </p>
                <Link to="/SignUp" className="btn btn-success">
                  Sign Up
                </Link>
              </div>
              <div className="image-container">
                <img
                  src="https://via.placeholder.com/800x400"
                  className="d-block w-100"
                  alt="Sign Up Banner"
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
