import {  useEffect, useState } from "react"
import { Header } from "./Header"
import { useParams } from "react-router-dom"

import { Loading } from "./Loading"
import { useDispatch } from "react-redux"
import { ADDTOCART, REMOVEFROMCART } from "../Store/CartOpe"

export const ProductDetails = ()=>{
    let[product,setProduct] = useState([])

    let {id}= useParams()
    console.log(id)
    useEffect(() => {
        // Fetch product details by matching id
        const foundProduct = BigDiscount.find((p) => p.id === parseInt(id));
        setProduct(foundProduct);
      }, [id]);

    let dispatch = useDispatch()

    let handleAddToCart = () =>{
        dispatch(ADDTOCART({...product,quantiy:1}))
    }

    let handleRemoveCart = () =>{
        dispatch(REMOVEFROMCART(product.id))
    }
  
    if (!product) {
        // Show a loading spinner or message if product is not found yet
        return (
          <div className="container">
            <Header />
            <h1>Loading product details...</h1>
          </div>
        );
      }
    
    return(
        <div className="container">
            <Header/>
            <h1>Single Product</h1>
            {
                (Object.keys(product).length)  ? (
                <div className="container">
                    <div className="row">
                        <div className="col-md-6">
                      <img src={product.image} alt="" width={'300px'} height={'300px'} className="img-fluid"/>
                     
                        </div>
                        <div className="col-md-6">
                            <h1>{product.title}</h1>
                            <h5>{product.description}</h5>
                            <strong className="bg-primary p-2 rounded-pill">{product.price}</strong>
                            <span>{product.ratting}</span>
                            <div>
                                <button className="btn btn-primary ms-2 mt-4" onClick={()=>handleAddToCart()}>ADD TO CART</button>
                                <button className="btn btn-danger ms-2 mt-4" onClick={()=>handleRemoveCart()}>REMOVE FROM CART</button>
                            </div>
                        </div>
                    </div>


                </div>
                
                
                ): (<div><Loading/></div>)
            }
        </div>
    )
}
