import { useEffect, useState } from "react"
import { Header } from "../Header"
import { useParams } from "react-router-dom"
import axios from "axios"
import { Loading } from "./Loading"
import { useDispatch } from "react-redux"
import { ADDTOCART, REMOVEFROMCART } from "../../store/CartOpe"

export const SingleProduct = ()=>{
    let[product,setProduct] = useState([])

    let {id}= useParams()
    console.log(id)

    let dispatch = useDispatch()

    let handleAddToCart = () =>{
        dispatch(ADDTOCART({...product,quantiy:1}))
    }

    let handleRemoveCart = () =>{
        dispatch(REMOVEFROMCART(product.id))
    }
    useEffect(()=>{
    let productData = async()=>{
        let productApi= await axios.get(`https://fakestoreapi.com/products/${id}`)
        console.log(productApi.data)
        setProduct(productApi.data)
    }
    productData()
    },[])
    return(
        <div className="container">
            <Header/>
            <h1>Single Product</h1>
            {
                (Object.keys(product).length)  ? (
                <div className="container">
                    <div className="row">
                        <div className="col-md-6">
                      <img src={product.image} alt="" width={'300px'} height={'300px'} className="img-fluid"/>
                     
                        </div>
                        <div className="col-md-6">
                            <h1>{product.title}</h1>
                            <h5>{product.description}</h5>
                            <strong className="bg-primary p-2 rounded-pill">{product.price}</strong>
                            <span>{product.ratting}</span>
                            {/* <div>
                                <button className="btn btn-primary ms-2 mt-4" onClick={()=>handleAddToCart()}>ADD TO CART</button>
                                <button className="btn btn-danger ms-2 mt-4" onClick={()=>handleRemoveCart()}>REMOVE FROM CART</button>
                            </div> */}
                        </div>
                    </div>


                </div>
                
                
                ): (<div><Loading/></div>)
            }
        </div>
    )
}
